import PropTypes from 'prop-types';

import s from './Contacts.module.css'

const Contacts = ({ removeUser ,getFilteredUsers, handleFilter }) => {
  const elements = getFilteredUsers.map(({ id, name, number }) => (
    <li className={s.item} key={id}>
      {name}: {number} <button className={s.btn} onClick={() => removeUser(id)}>Delete</button>
    </li>
  ));

  return (
    <>
      <label>
        Find contacts by name
        <input className={s.input}
          type="text"
          name="filter"
          placeholder="enter the name"
          onChange={handleFilter}
        ></input>
      </label>
      <ul>{elements}</ul>
    </>
  );
};

Users.propTypes = {
  removeUser: PropTypes.func.isRequired,
  getFilteredUsers: PropTypes.array,
  handleFilter: PropTypes.func.isRequired,
}

export default Contacts;
